import React from 'react';
import './App.css';
import { Container, Flex, Heading } from '@chakra-ui/layout';
import { Button, Text } from '@chakra-ui/react';
import { Image } from '@chakra-ui/image';
//import { Button } from '@chakra-ui/button';

function App() {
  return (
    <div className="App">

  {/* Section-1 */}

      <Flex className="Section-1">

        {/* Article */}
        <Container maxW="40%">
          <Heading 
            className="heading-title"
            mt='143px'
            textAlign='left'
            w='md'
          >find interior design inspiration for your beloved home</Heading>
          <Text 
            className="heading-sub-title"
            mt='27px'
            textAlign='left'
            w='70%'
          >Consectetur adipiscing elit ut aliquam, purus sit amet luctus venenatis</Text>

          {/* Button */}
          <Button
            className="button"
            display="block"
            backgroundColor="#1D6861"
            borderRadius="none"
            mt="52px"
            _hover={{ bg: "#18524c" }}
          >
            Take a Tour
          </Button>
        </Container>

        {/* Image-Slider */}
        <Container maxWidth="60%">
            <Image 
            w='606px'
            h='620px' 
            src="https://images.unsplash.com/photo-1583847268964-b28dc8f51f92?ixid=MnwxMjA3fDB8MHxzZWFyY2h8NXx8aW50ZXJpb3J8ZW58MHx8MHx8&ixlib=rb-1.2.1&auto=format&fit=crop&w=500&q=60" 
             />
        </Container>
      </Flex>
    </div>
  );
}

export default App;
