import React from 'react';
import './App.css';
import { Container, Flex, Heading } from '@chakra-ui/layout';
import { Button, Text } from '@chakra-ui/react';
import { Image } from '@chakra-ui/image';
//import { Button } from '@chakra-ui/button';

function App() {
  return (
    <div className="App">

  {/* Section-1 */}

      <Flex className="Section-1">

        {/* Article */}
        <Container>
          <Heading 
            className="heading-title"
            mt='143px'
            textAlign='left'
            w='md'
          >find interior design inspiration for your beloved home</Heading>
          <Text 
            className="heading-sub-title"
            mt='27px'
            textAlign='left'
            w='60%'
          >Consectetur adipiscing elit ut aliquam, purus sit amet luctus venenatis</Text>

          {/* Button */}
          <Button
            className="button"
            display="block"
            backgroundColor="#1D6861"
            borderRadius="none"
            mt="52px"
            _hover={{ bg: "#18524c" }}
          >
            Take a Tour
          </Button>
        </Container>

        {/* Image-Slider */}
        <Container>
            <Image src="https://www.icreate.id/assets/images/product/pic1/000297.jpg" alt="image-1" />
        </Container>
      </Flex>
    </div>
  );
}

export default App;
