import * as React from 'react';
import './App.css';
import { Container, Flex, Heading } from '@chakra-ui/layout';
import { Button, Text, Tooltip } from '@chakra-ui/react';
import { Image } from '@chakra-ui/image';
//import { Button } from '@chakra-ui/button';

function App() {
  return (
    <div className="App">

  {/* Section-1 */}

      <Flex 
      className="Section-row"
      direction={{base: "column", md:"row"}}
      justifyContent={{base: "center", md:"flex-start"}}>

        {/* Article */}
        <Container maxW="40%" minW="10%" ml='104px'>
          <Heading 
            className="heading-title"
            mt='143px'
            textAlign='left'
            fontSize={{ base: "24px", md: "30px", lg: "36px" }}
            w='md'
          >find interior design inspiration for your beloved home</Heading>
          <Text 
            className="heading-sub-title"
            mt='27px'
            textAlign='left'
            w='50%'
          >Consectetur adipiscing elit ut aliquam, purus sit amet luctus venenatis</Text>

          {/* Button */}
          <Tooltip HasArrow Label="Search Your Furniture" bg="green.600">
          <Button
            className="button"
            backgroundColor="#1D6861"
            borderRadius="none"
            mt="52px"
            _hover={{ bg: "#18524c" }}
          >
            Take a Tour
          </Button>
          </Tooltip>
        </Container>

        {/* Image-Slider */}
        <Container maxWidth="60%" minW="20%" mr='104px'>
            <Image 
            w={[306 , 506 , 706]}
            h={[320 , 520 , 720]} 
            src="https://images.unsplash.com/photo-1583847268964-b28dc8f51f92?ixid=MnwxMjA3fDB8MHxzZWFyY2h8NXx8aW50ZXJpb3J8ZW58MHx8MHx8&ixlib=rb-1.2.1&auto=format&fit=crop&w=500&q=60" 
             />
        </Container>
      </Flex>
    </div>
  );
}

export default App;
